mapper.select:
SELECT SUM(TCKR.EXAM_RES)
FROM tm_cm_kdsqc_res AS TCKR
WHERE 1=1
AND TCKR.R_NUMBER = #{rNumber}
AND TCKR.TRGTER = #{trgter}
AND TCKR.R_NUMBER_SEQ = #{rNumberSeq}

SELECT IFNULL(
	(SELECT MAX(TCKR.R_NUMBER_SEQ)
	FROM tm_cm_kdsqc_res AS TCKR
	WHERE 1=1
	AND TCKR.R_NUMBER = #{rNumber}
	), 0) AS rNumberSeq
